            let [dxn, dyn] = [sfm[0] / this._focal, sfm[1] / this._focal];
            const dr = Math.sqrt(dxn * dxn + dyn * dyn);
            let d = this._distortionFromDistortedRadius(dr, this._ck1, this._ck2, this._radialPeak);
